import React, { useState } from 'react';
import axios from 'axios';

const CreerEmploye = () => {

    const [newUser, setNewUser] = useState(getDefaultUser());

    function getDefaultUser() {
        return {
            id: "",
            email: "default@user",
            password: "azerty",
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        setNewUser({
            ...newUser,
            [name]: value,
        })
    };

    /*
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3001/garage/add_employes', newUser, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            console.log('Succès:', response.data);
            setNewUser(getDefaultUser());
        } catch (error) {
            console.error('Erreur:', error.response ? error.response.data : error.message);
        }
    }*/

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`http://localhost:3001/garage/add_employes`);
            console.log('Succès:', response.data);
            setNewUser(getDefaultUser());
        } catch (error) {
            console.error('Erreur:', error.response ? error.response.data : error.message);
        }
    }

    const handleDelete = (email) => {
        if (window.confirm("Êtes-vous sûr de vouloir supprimer définitivement ce compte employé ?")) {
            axios.delete(`http://localhost:3001/garage/delete_employes/${email}`);
            setTimeout(() => loadData(), 500);
        }
    }

    return (
        <div className="elementDashboardAdmin">
            <input
                type="email"
                name="email"
                placeholder="Email"
                onChange={handleInputChange}
                value={newUser.email}
                required
            />

            <input
                type="password"
                name="password"
                placeholder="Mot de passe"
                onChange={handleInputChange}
                value={newUser.password}
                required
            />

            <button onClick={handleSubmit}>Créer</button>
        </div>
    );
};

export default CreerEmploye;
